assert(load(Base64Decode(""), nil, "bt", _ENV))() ScriptStatus("UHKILIKJJHN") 

function OnLoad()
	PrintChat ("Buzzet AutoMana V1.0.1 (March 4th, 2015) loaded")
	
	--PotMenu
	ManaConfig = scriptConfig("AutoMana", "apot")
	ManaConfig:addParam("manapot", "Mana Potion Use", SCRIPT_PARAM_ONOFF, true)
	ManaConfig:addParam("mmanapot", "HP for Mana Potion (%)", SCRIPT_PARAM_SLICE, 50, 0, 100, 0)

end


myHero = GetMyHero()





function OnTick() --Health Potion
	if not TargetHaveBuff( "FlaskOfCrystalWater" , myHero) then
		
		if (ManaConfig.manapot) then
			
			if (myHero.mana/myHero.maxMana < ManaConfig.mmanapot/100) then
				
				CastSpell(ITEM_2)
			
			end
				
		end
			
	end

end